import { toUint8Array } from "https://deno.land/x/base64/mod.ts";

export interface Wasm {
  buffer: Uint8Array;
  memory: Uint8Array;
  exports: { [key: string]: any };
  lalloc: (size: number) => void;
}

export function loadWasm(): Wasm {
  const wasm: Wasm = {} as Wasm;
  wasm.buffer = toUint8Array(
    ""
  );
  wasm.exports = new WebAssembly.Instance(
    new WebAssembly.Module(wasm.buffer)
  ).exports;
  wasm.memory = new Uint8Array(wasm.exports.memory.buffer);
  wasm.lalloc = (bytes: number): void => {
    if (wasm.memory.byteLength < bytes) {
      const pages: number = Math.ceil((bytes - wasm.memory.byteLength) / 65536);
      wasm.exports.memory.grow(pages);
      wasm.memory = new Uint8Array(wasm.exports.memory.buffer);
    }
  };
  return wasm;
}